import {createStore} from 'redux';
import {createSlice} from '@reduxjs/toolkit';

const initialState = { counter: 0, showCounter: true };

const counterSlice = createSlice({
	name: 'counter',
	initialState: initialState,
	reducers: {
		increment(state) {
			//!!! Redux toolkit uses a package that will detect cases like this and 
			//clone state instead of changing the existing one
			//This is mutable-looking way but not mutable
			state.counter++;
		},
		decrement(state) {
			state.counter--;
		},
		increase(state, action) {
			state.counter = state.counter + action.amount;
		},
		toggleCounter(state) {
			state.showCounter = !state.showCounter;
		}
	}
})

const counterReducer = (state=initialState, action) => {

	//We override the old state, return brand new object, not mutate it or change existing state
	if(action.type === 'increment') {
		return {
			counter: state.counter + 1,
			showCounter: state.showCounter
		}
	}

	if(action.type === 'increase') {
		return {
			counter: state.counter + action.amount,
			showCounter: state.showCounter
		}
	}


	if(action.type === 'decrement') {
		return {
			counter: state.counter - 1,
			showCounter: state.showCounter
		}
	}

	if(action.type === 'toggle') {
		return {
			showCounter: !state.showCounter,
			counter: state.counter
		}
	}
	return state;

}

const store = createStore(counterReducer);

export default store;
